#!/usr/bin/env python3"""This script merges gene counts from STAR --quantMode using unstranded column (recommended for GDC data)Designed for expression files from GDC datasets"""### ---------------------------------------- ###def load_data(f, suffix):        sample = f.replace(suffix, '')        data = pd.read_csv(f, sep='\t', header=None, skiprows=6, index_col=None).iloc[:, [0, 1, 3]]    data.columns = ['GeneID', 'GeneSymbol', sample]        return data### ------------------MAIN------------------ ###import pandas as pdfrom os import listdirfrom sys import argvfile_suffix = argv[argv.index('--file_suffix') + 1]### Get file listfiles = [file for file in listdir() if file.endswith(file_suffix)]files.sort()### Merge filestry:        del all_data    except:        passfor file in files:        data = load_data(file, file_suffix)        try:                all_data = all_data.merge(data.copy(), how='inner', on=['GeneID', 'GeneSymbol'])        except:                all_data = data.copy()    all_data.to_csv('MergedGeneCounts.tsv', sep='\t', index=False, header=True)