#!/usr/bin/env python3"""This script calculates the size of contigs from a fasta file and prints them in format used by vcf files"""### ---------------------------------------- ###def get_contigs(path):        contig_sizes = {}        with open(path) as fasta:                for f in fasta:                        f = f.replace('\n', '')                        if f.startswith('>'):                                contig_name = f.split(' ')[0].replace('>', '')                contig_sizes[contig_name] = 0                            else:                                bases = len(f)                contig_sizes[contig_name] += bases                    return contig_sizes### ------------------MAIN------------------ ###from sys import argv### PARSE DATA ----------------------------- #### Get fasta pathfasta_path = argv[argv.index("--fasta") + 1]# Import file and gets contigscontig_sizes = get_contigs(fasta_path)# Write to fileout_name = fasta_path.split('/')[-1].replace('.fa', '').replace('.fna', '').replace('.fasta', '') + '_contigsizes.txt'out_text = '\n'.join([f'##contig=<ID={contig},length={length}>' for contig,length in contig_sizes.items()])with open(out_name, 'w') as out_file:        out_file.write(out_text)